import { Injectable } from '@angular/core';
import axios from "axios"

@Injectable({
  providedIn: 'root'
})
export class SwapiService {

  constructor() { }
    getPersonajes (id:any):void{
      axios.get("https://swapi.dev/api/films")
             .then(data=>{
               let data1=data.data.results.filter((ele:any)=>{
                       if(id==ele.episode_id){ return ele  } else return null
              })
               return data1[0].characters
             })


} }
