import { Component, OnInit} from '@angular/core';
import { FilmComponent } from '../film/film.component';

import axios from "axios"

@Component({
  selector: 'app-films',
  templateUrl: './films.component.html',
  styleUrls: ['./films.component.css']
})
export class FilmsComponent implements OnInit{
 films:string[]=[];


ngOnInit():void{
  axios("https://swapi.dev/api/films")
       .then((films)=>{ return films.data.results})
       .then((films)=>{
        const data= films.map((ele:any)=>{return ele.title})
        return data;
       })
       .then(ele=>{ 
        this.films=ele;
       })

}
}
<p>films works!</p>
<h1>{{nombre}}</h1>
<app-film [films]="films"></app-film>
<app-film [nombre]="nombre"></app-film>
