import axios from "axios"

@Injectable({
  providedIn: 'root'
})
export class SwapiService {

  constructor() { }
    getPersonajes(id:any){
     return axios.get("https://swapi.dev/api/films")
             .then(data=>{
              let data1=data.data.results.filter((ele:any)=>{
                       if(id===ele.episode_id){
