import { Component} from '@angular/core';
import axios from "axios"

@Component({
  selector: 'app-films',
  templateUrl: './films.component.html',
  styleUrls: ['./films.component.css']
})
export class FilmsComponent {
  films:any=[];
  id:any=[1,2,3,4,5,6]
  i:any; 
constructor(){
}
async ngOnInit(){
  let datos= await axios.get("https://swapi.dev/api/films")
  this.films=datos.data.results.map((ele:any)=>{
    return{
      nombre:ele.title,
      id:ele.episode_id,
      director:ele.director,
      personajes:"Personajes"
    }
  })
}

tomarId(event:any){
  console.log(console.log(event.target.value))

}

}

<header>Pagina de Films</header>
<div *ngFor="let i of films">
    <span>{{i.nombre}}</span>
    <hr>
    <span>Director:{{i.director}}</span>
    <hr>

    <span >{{i.personajes}}</span>
    <span>Numero del Film:{{i.id}}</span>

</div>
<div
*ngFor="let j of  id">

<div routerLink="Personajes">
    <option (click)="tomarId($event)" [value]="[i]">Personajes de Film Numero {{i}}</option>
</div> 
</div>
