import { Injectable } from '@angular/core';
import axios from "axios"

@Injectable({
  providedIn: 'root'
})
export class SwapiService {
  constructor() { }
    getPersonajes (id:any){
     let arrUrl:any= axios.get("https://swapi.dev/api/films")
             .then(data=>{
               return data.data.results.filter((ele:any)=>{
                       if(id==ele.episode_id){ 
                        let objeto={
                          personajes:ele.characters
                        }
                        return objeto
                        } else return null
              })

             }) 
             .then(data=>{return data[0].characters})

   return Promise.all(arrUrl).then((res)=>{return res})
}

}
