import { Component} from '@angular/core';
import { TomarDatos } from 'src/app/ngrx/Actions'; 

import axios from "axios"

@Component({
  selector: 'app-films',
  templateUrl: './films.component.html',
  styleUrls: ['./films.component.css']
})
export class FilmsComponent {
  films:any=[];
  i:any; 
constructor(){
}
async ngOnInit(){
  let datos= await axios.get("https://swapi.dev/api/films")
  this.films=datos.data.results.map((ele:any)=>{
    return{
      nombre:ele.title,
      id:ele.episode_id,
      director:ele.director,
      personajes:"Personajes"
    }
  })
}

 tomarId(event:any){
  let id:number=event.target.id;
   TomarDatos(id)

}}

<header>Pagina de Films</header>
<div *ngFor="let i of films">
    <span>{{i.nombre}}</span>
    <hr>
    <span>Director:{{i.director}}</span>
    <hr>

    <span >{{i.personajes}}</span>
    <span>Numero del Film:{{i.id}}</span>

</div>

<div routerLink="Personajes">
    <button (click)="tomarId($event)"  id="1" >Personajes de Film Numero 1</button>
    <button (click)="tomarId($event)"  id="2" >Personajes de Film Numero 2</button>
    <button (click)="tomarId($event)"  id="3" >Personajes de Film Numero 3</button>
    <button (click)="tomarId($event)"  id="4" >Personajes de Film Numero 4</button>
    <button (click)="tomarId($event)"  id="5" >Personajes de Film Numero 5</button>
    <button (click)="tomarId($event)"  id="6" >Personajes de Film Numero 6</button>

</div>
