import { Component} from '@angular/core';
import axios from "axios"
import { RouterLinkActive } from '@angular/router';

@Component({
  selector: 'app-films',
  templateUrl: './films.component.html',
  styleUrls: ['./films.component.css']
})
export class FilmsComponent {
  films:any=[];
constructor(){
}
async ngOnInit(){
  let datos= await axios.get("https://swapi.dev/api/films")
  this.films=datos.data.results.map((ele:any)=>{
    return{
      nombre:ele.title,
      id:ele.episode_id,
      director:ele.director,
      personajes:"Personajes"
    }
  })


}
}

<header>Pagina de Films</header>
<div *ngFor="let i of films">
    <span>{{i.nombre}}</span>
    <hr>
    <span>Director:{{i.director}}</span>
    <hr>
    <div [routerLink]="['Persnajes/:id']"  >
    <span  >{{i.personajes}}</span> 
    </div>

</div>
